import React from 'react';
import { useCurrencyLayer } from '../hooks/currencyLayer';
import './CurrencyPicker.css';

const CurrencyPicker = props => {
  const [isLoading, fetchedData] = useCurrencyLayer(
    { selectedCurrency: props.selectedCurrency,
    currencies: ['EUR','CHF'], date: ['2020-01-01'] }, []);

  // const latestCurrencies = fetchedData
  let latestCurrencies;
  if (fetchedData && fetchedData.length <= 2) {
    const res = fetchedData.slice(-1)[0].currencyValues;
    latestCurrencies =  Object.keys(res).map((currency, index) => ({
        id: index + 1,
        name: currency,
        value: res[currency]
      }));
  } else {
    latestCurrencies = []
  }

  let content = <p>Loading Currencys...</p>;

  if (!isLoading && latestCurrencies && latestCurrencies.length > 0) {
    content = (
      <select
        onChange={props.onCurrencySelect}
        value={props.selectedCurrency.key}
      >
        {latestCurrencies.map(currency => (
          <option key={currency.id} value={currency.name}>
            {currency.name}
          </option>
        ))}
      </select>
    );
  } else if (
    !isLoading &&
    (!latestCurrencies || latestCurrencies.length === 0)
  ) {
    content = <p>Could not fetch any data.</p>;
  }
  return content;
};

export default CurrencyPicker;
