import { useCurrencyLayer } from '../hooks/currencyLayer';
import Summary from './Summary';
import React, { useState } from 'react';

const Currency = props => {
  const [date, setdate] = useState(['2020-01-01', '2020-01-02']);

  const [isLoading, fetchedData] = useCurrencyLayer(
    {selectedCurrency: props.selectedCurrency, date: ['2020-01-01', '2020-01-02'], currencies: ['EUR','CHF']},
    [props.selectedCurrency]
  );

  useEffect(() => {
    return () => {
      let loadedCurrency = null;
      let currencyValues = [];
      let currencyDate = [];
      if (fetchedData && fetchedData.length === 2) {
       fetchedData.forEach((data) => {
         debugger;
         currencyValues.push(data.currencyValues[props.selectedCurrency]);
         currencyDate.push(data.date);
          loadedCurrency = {
            id: props.selectedCurrency,
            currencyValue: currencyValues,
            dates: currencyDate
          };
        })
        debugger;
      }
        };
  }, []);

  let content = <p>Loading Currency...</p>;

  if (!isLoading && loadedCurrency) {
    content = (
      <Summary
        selectedCurrency={props.selectedCurrency || []}
        currencyValues={loadedCurrency.currencyValue || []}
        dates={date || []}
      />
    );
  } else if (!isLoading && !loadedCurrency) {
    content = <p>Failed to fetch Currency.</p>;
  }
  return content;
};

export default React.memo(Currency);
