import { useState, useEffect } from 'react';
import CurrencyLayerClient from 'currencylayer-client';

export const useCurrencyLayer = (params, dependencies) => {
  const [isLoading, setIsLoading] = useState(false);
  const [fetchedData, setFetchedData] = useState(null);

  useEffect(() => {
    setIsLoading(true);
    const client = new CurrencyLayerClient({apiKey: '531d6475cefb59a4563e9acbef4b0559'});
    if (params && params.date) {
      const result = [];
      params.date.map( async (d) => {
        const requestedParams = {currencies: params.currencies, date: d}
        await client.historical(requestedParams).then(response => {
          if (!response.success) {
            throw new Error('Failed to fetch.');
          } else {
            result.push({ date: d, currencyValues: response.quotes})
            debugger;
          }
          setFetchedData(result);
          setIsLoading(false);

        })
        .catch(err => {
          console.log(err);
          setIsLoading(false);
        });
      })
    }
  }, dependencies);

  return [isLoading, fetchedData];
};
