import React, { useEffect } from 'react';
import { useCurrencyLayer } from '../hooks/currencyLayer';
import Summary from './Summary';

const Currency = props => {
    const dates = ['1999-02-03', '1999-02-04'];
    // const result = [];
    const result = (props.chosenSide === 'realtime') ? useCurrencyLayer(
      {chosenSide: props.chosenSide , currencies: [props.selectedCurrency]},
      [props.selectedCurrency]
    ) : dates.map((date)=> {
        useCurrencyLayer(
        { selectedCurrency: props.selectedCurrency,
          chosenSide: 'weekly',
          currencies: ['EUR','CHF'], date: date }, []);
    });
    debugger;
  let loadedCurrency = null;

  if (result.fetchedData) {
    loadedCurrency = {
      id: props.selectedCurrency,
    };
  }

  // useEffect(() => {
  //   return () => {
  //     console.log('component did unmount');
  //   };
  // }, []);

  let content = <p>Loading Currency...</p>;

  if (!result.isLoading && loadedCurrency) {
    content = (
      <Summary
        name={loadedCurrency.id}
        chosenSide={props.chosenSide}
      />
    );
  } else if (!isLoading && !loadedCurrency) {
    content = <p>Failed to fetch Currency.</p>;
  }
  return content;
};

export default React.memo(Currency);
