import { useState, useEffect } from 'react';
import CurrencyLayerClient from 'currencylayer-client';

export const useCurrencyLayer = (params, dependencies) => {
  const [isLoading, setIsLoading] = useState(false);
  const [fetchedData, setFetchedData] = useState(null);

  useEffect(() => {
    setIsLoading(true);
    const client = new CurrencyLayerClient({apiKey: '531d6475cefb59a4563e9acbef4b0559'});
      const fetchData = async () => {
        if (params.choseSide === 'reltime') {
          return await client.live({source: 'USD', currencies: ['EUR', 'CHF']});
        }
        const result = []
        params.dates.map( async (date) => {
          result.push(client.historical(
            { 
              date: date,
              currencies: ['EUR', 'CHF'],
          }))
        })
        return result;
    }

    fetchData().then(response => {
        if (!response.success) {
          throw new Error('Failed to fetch.');
        } else if (response.success && params.choseSide === 'weekly') {
          response.map(data => {debugger;})
        } else {
          setIsLoading(false);
          setFetchedData(response.quotes);
        }
      })
      .catch(err => {
        console.log(err);
        setIsLoading(false);
      });
  }, dependencies);

  return [isLoading, fetchedData];
};
