import { useState, useEffect } from 'react';
import CurrencyLayerClient from 'currencylayer-client';

export const useCurrencyLayer = (params, dependencies) => {
  const [isLoading, setIsLoading] = useState(false);
  const [fetchedData, setFetchedData] = useState(null);

  useEffect(() => {
    setIsLoading(true);
    const client = new CurrencyLayerClient({apiKey: '531d6475cefb59a4563e9acbef4b0559'});
      const fetchData = async () => {
        if (params.chosenSide === 'reatime') {
          return await client['live']({source: 'USD', currencies: params.currencies});
        } else {
          const result = []
          const dates = ['2021-01-01', '2021-01-02', '2021-01-03', '2021-01-04' ,'2021-01-05' ,'2021-01-06']
          dates.map(async (date) => {
            result.push( await client.historical(
              { 
                date: date,
                currencies: ['EUR', 'CHF'],
            }))
          })
          return result;
          // return await client['historical']({source: 'USD', currencies: params.currencies, date: '2002-09-24'});

        }
    }
    debugger;
    fetchData().then(response => {
        if (!response.success) {
          throw new Error('Failed to fetch.');
        } else if (response.success && params.choseSide === 'ealtime') {
          response.map(data => {debugger;})
        } else {
          setIsLoading(false);
          setFetchedData(response.quotes);
        }
      })
      .catch(err => {
        console.log(err);
        setIsLoading(false);
      });
  }, dependencies);

  return [isLoading, fetchedData];
};
