import { useCurrencyLayer } from '../hooks/currencyLayer';
import './CurrencyPicker.css';
import Summary from './Summary';
import React, { useState } from 'react';

const CurrencyPicker = () => {
  const [selectedCurrency, setSelectedCurrency] = useState('USDEUR');
  const [isLoading, fetchedData] = useCurrencyLayer(
    { selectedCurrency, currencies: ['EUR','CHF'], date: [
      '2020-01-01',
      '2020-01-02',
      '2020-01-03',
      '2020-01-04',
      '2020-01-05', '2020-01-06' , '2020-02-06'] }, []);
  let latestCurrencies;
  const currencyValues = [];
  const currencyDate = [];

  const currencySelectHandler = event => {
      const currency = event.target.value;
      setSelectedCurrency(currency);
  };

  const currencyName = name => {
    let unwantedString = name.includes('EUR') ? 'EUR' : 'USD';
    return name.replace(unwantedString, '')
  };

  let test;
  let eurWeeklyValues;
  let chfWeeklyValues = [];
  const changeNormToEur = () => {

    if (currencyName(selectedCurrency) === 'USD' && currencyValues.length === 7) {
      eurWeeklyValues = currencyValues;
      test = currencyValues.map((currencyValue) => {
        let r = 1 - currencyValue;
        let c = r / currencyValue;
        let d = c * 1;
        return r = (d + 1).toFixed(2);
      })
    } else if (currencyName(selectedCurrency) === 'CHF' && currencyValues.length === 7 && eurWeeklyValues) {
      chfWeeklyValues = currencyValues;
      debugger;
    } else {
      test = currencyValues;
    }
  };

  if (fetchedData) {
    fetchedData.forEach((data, index) => {
      if (index === 6) {
        latestCurrencies = Object.keys(data.currencyValues).map((currency, index) => ({
          id: index + 1,
          name: currency,
        }));
      }
      currencyValues.push(data.currencyValues[selectedCurrency]);
      if (currencyName(selectedCurrency) === 'CHF') {
        debugger;
        let eurToChf = data.currencyValues[selectedCurrency] / data.currencyValues['USDCHF'];
        currencyValues.push(eurToChf.toFixed(2))
      } else {
        let r = 1 - data.currencyValues[selectedCurrency];
        let c = r / data.currencyValues[selectedCurrency];
        let d = c * 1;
        debugger;
        currencyValues.push((d + 1).toFixed(2))
      }
      let dateArr = data.date.split('-');
      let monthDayFormat =[dateArr[1], dateArr[2]].join('-');
      currencyDate.push(monthDayFormat);
    })
    // changeNormToEur();
  }

  let content = <p>Loading Currencys...</p>;

  if (!isLoading && latestCurrencies && latestCurrencies.length > 0) {
    content = (
      <>
        <select
          onChange={currencySelectHandler}
          value={selectedCurrency}
        >
          {latestCurrencies.map(currency => (
            <option key={currency.id} value={currency.name}>
              {currencyName(currency.name)}
            </option>
          ))}
        </select>
        <br/>
        <Summary
          selectedCurrency={currencyName(selectedCurrency)}
          currencyValues={currencyValues}
          dates={currencyDate}
        />
      </>
    );
  } else if (
    !isLoading &&
    (!latestCurrencies || latestCurrencies.length === 0)
  ) {
    content = <p>Could not fetch any data.</p>;
  }
  return content;
};

export default CurrencyPicker;
