import React from 'react';
import { Line } from '@reactchartjs/react-chart.js';
const Summary = props => {
let test;
if (props.selectedCurrency === 'USD') {
  test = props.currencyValues.map((currencyValue) => {
    let r = 1 - currencyValue;
    let c = r / currencyValue;
    let d = c * 1;
    return r = (d + 1).toFixed(2);
  })
} else {
  test = props.selectedCurrency;
}

const data = {
  labels: props.dates,
  datasets: [
    {
      label: '# of Votes',
      data: test,
      fill: false,
      backgroundColor: 'rgb(255, 99, 132)',
      borderColor: 'rgba(255, 99, 132, 0.2)',
    },
  ],
};

const options = {
  scales: {
    yAxes: [
      {
        ticks: {
          beginAtZero: true,
        },
      },
    ],
  },
};


  return <>
    <div className='header'>
      <h1 className='title'>{props.selectedCurrency}</h1>
    </div>
    <Line data={data} options={options} />
  </>
};

export default Summary;
