import React from 'react';
import { useCurrencyLayer } from '../hooks/currencyLayer';
import './CurrencyPicker.css';

const CurrencyPicker = props => {
  const [isLoading, fetchedData] = useCurrencyLayer(
    { selectedCurrency: props.selectedCurrency,
      chosenSide: props.chosenSide,
      currencies: ['EUR','CHF'] }, []);

  const selectedCurrencys = fetchedData
    ? Object.keys(fetchedData).map((currency, index) => ({
        id: index + 1,
        name: currency,
        value: fetchedData[currency]
      }))
    : [];
  debugger;
  let content = <p>Loading Currencys...</p>;

  if (!isLoading && selectedCurrencys && selectedCurrencys.length > 0) {
    content = (
      <select
        onChange={props.onCurrencySelect}
        value={props.selectedCurrency.key}
        className={props.side}
      >
        {selectedCurrencys.map(currency => (
          <option key={currency.id} value={currency.name}>
            {currency.name}
          </option>
        ))}
      </select>
    );}
  // } else if (
  //   !isLoading &&
  //   (!selectedCurrencys || selectedCurrencys.length === 0)
  // ) {
  //   content = <p>Could not fetch any data.</p>;
  // }
  return content;
};

export default CurrencyPicker;
