import React from 'react';

import CurrencyPicker from './components/CurrencyPicker';

const App = () => {
  const weeklyDatesArr = [];
  const getLastWeekDates = () => {
    [0,1,2,3,4,5,6].map((d) => {
      const date = new Date();
      const day = date.getDate() - d;
      const month = date.getMonth() + 1;
      const year = date.getFullYear();
      // let formatedDay = Number(day) >= 10 ? day : '0' + day;
      // let formatedMonth = Number(month) >= 10 ? month : '0' + month;
      let historicalDate = [year, month, day].join('-');
      weeklyDatesArr.push(historicalDate);
    })
  };

  getLastWeekDates()

  let content = <p>Loading ...</p>
  if (weeklyDatesArr.length === 7) {
    content = (
      <React.Fragment>
        <CurrencyPicker dates={weeklyDatesArr} />
      </React.Fragment>
    );
  }
  return content;
};

export default App;
