import React from 'react';

import CurrencyPicker from './components/CurrencyPicker';

const App = () => {
  const datesArr = [];
  const getLastWeekDates = () => {
    const date = new Date();
    const year = date.getFullYear();
    const month = date.getMonth() + 1;
    const day = date.getDate();
    [0,1,2,3,4,5,6].map((d) => {
      let dayInPast = Number(day) - d;
      let formatedDay = dayInPast >= 10 ? dayInPast.toString() : `0${dayInPast}`
      let historicalDate = [year, month, formatedDay].join('-');
      datesArr.push(historicalDate);
    })
  };

  getLastWeekDates()

  let content = <p>Loading ...</p>
  content = (
    <React.Fragment>
      <CurrencyPicker dates={datesArr} />
    </React.Fragment>
  );
  return content;
};

export default App;
