import CurrencyPicker from './components/CurrencyPicker';
import Currency from './components/Currency';

const App = () => {
  const [selectedCurrency, setSelectedCurrency] = useState('EUR');
  // const [chosenSide, setChosenSide] = useState('realtime');

  const sideHandler = side => {
    setChosenSide(side);
  };

  const currencySelectHandler = event => {
    const currency = event.target.value;
    setSelectedCurrency(currency);
  };

  let content = (
    <React.Fragment>
      <CurrencyPicker
        side={chosenSide}
        selectedCurrency={selectedCurrency}
        onCurrencySelect={currencySelectHandler}
      />
      <Currency selectedCurrency={selectedCurrency} chosenSide={chosenSide} />
      {/* <button onClick={sideHandler.bind(this, 'realtime')}>realtime Side</button>
      <button onClick={sideHandler.bind(this, 'weekly')}>Weekly Side</button> */}
    </React.Fragment>
  );
  return content;
};

export default App;
